package backstage

import (
	"testing"
)

func TestListAPIs(t *testing.T) {
	ts := CreateServer(t)
	defer ts.Close()

	// Get with no args calls List
	str, err := SetupBackstageTestRESTClient(ts).GetAPI()

	AssertError(t, err)
	AssertLineCompare(t, str, apis, 0)
}

func TestGetAPIs(t *testing.T) {
	ts := CreateServer(t)
	defer ts.Close()

	nsName := "default:ollama-service-api"
	str, err := SetupBackstageTestRESTClient(ts).GetAPI(nsName)

	AssertError(t, err)
	AssertContains(t, str, nsName)
}

func TestGetAPIsError(t *testing.T) {
	ts := CreateServer(t)
	defer ts.Close()

	nsName := "404:404"
	_, err := SetupBackstageTestRESTClient(ts).GetAPI(nsName)
	if err == nil {
		t.Error("expected error")
	}
}

func TestGetAPIsWithTags(t *testing.T) {
	ts := CreateServer(t)
	defer ts.Close()

	bs := SetupBackstageTestRESTClient(ts)
	bs.Tags = true

	for _, tc := range []struct {
		args   []string
		str    string
		subset bool
	}{
		{
			args: []string{"genai", "meta"},
		},
		{
			args: []string{"vllm", "api", "openai"},
			str:  apisFromTagsNoSubset,
		},
		{
			args:   []string{"vllm"},
			subset: true,
			str:    apisFromTags,
		},
	} {
		bs.Subset = tc.subset
		str, err := bs.GetAPI(tc.args...)
		AssertError(t, err)
		AssertLineCompare(t, str, tc.str, 0)
	}
}

